mapper.insert:
INSERT INTO carts
user_id = #{userId}, menu_id = #{menuId},
quantity = #{quantity}

mapper.update:
UPDATE carts
SET quantity = #{quantity}
WHERE id = #{cartId}
AND user_id = #{userId}

mapper.delete:
DELETE
FROM carts
WHERE id = #{cartId}
AND user_id = #{userId}

mapper.select:
SELECT cm.cart_id , c.menu_id, cm.option_id, c.quantity
FROM cart c
LEFT JOIN cart_menu_option cm
ON c.id = cm.cart_id
WHERE cm.cart_id = #{cartId}
AND c.user_id = #{userId}
