mapper.insert:
INSERT INTO favorites
SET user_id = #{userId},
store_id = #{storeId}

mapper.select:
SELECT user_id
FROM favorites
WHERE store_id = #{storeId}
AND user_id = #{userId}

mapper.delete:
DELETE FROM favorites
WHERE user_id = #{userId}
AND store_id = #{storeId}

mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN TRUE ELSE FALSE END
FROM favorites
WHERE user_id = #{userId}
AND store_id = #{storeId}
