mapper.insert:
INSERT INTO menus
SET user_id = #{userId}
,   store_id = #{storeId}
,   name = #{name}
,   comment =#{comment}
,   price = #{price}
,   image_path = #{imagePath}

mapper.select:
SELECT id AS menuId, store_id, name, comment, price, image_path
FROM menus
WHERE id = #{menuId}

mapper.update:
UPDATE menus
SET name = #{name}
,   user_id = #{userId}
,   comment =#{comment}
,   price = #{price}

,   image_path = #{imagePath}

WHERE id = #{id}

mapper.delete:
DELETE FROM menus
WHERE id = #{menuId}
AND user_id = #{userId}
