mapper.update:
UPDATE reviews
SET rating = #{rating}
,   comment = #{comment}
,    image_path = #{imagePath}
WHERE id = #{id}

mapper.select:
SELECT pic
FROM review_image r
WHERE r.review_id ={reviewId}

SELECT r.id
FROM reviews r
JOIN orders o
ON o.id = r.order_id
WHERE r.id = #{reviewId}
AND o.store_id = #{storeId}

mapper.update:
UPDATE reviews
SET owner_comment = #{ownerComment}
WHERE id = #{reviewId}

mapper.delete:
DELETE FROM reviews
WHERE id = #{reviewId}
AND user_id = #{userId}

mapper.select:
SELECT r.id, r.order_id,o.store_id, o.user_id,r.rating, r.`comment`, r.created_at, r.is_hide, r.owner_comment FROM review r LEFT JOIN review_image ri ON r.id = ri.review_id LEFT JOIN orders o ON r.order_id = o.id LEFT JOIN orders_menu om ON o.id = om.order_id
o.store_id = #{storeId}
AND o.created_at BETWEEN CONCAT(#{startDate}, ' 00:00:00') AND CONCAT(#{endDate}, ' 23:59:59')
GROUP BY r.id LIMIT #{startIdx}, #{size}
